result:
.bar-positive {
  fill: steelblue;
  transition: r 0.2s ease-in-out;
}

.svg-container {
  display: inline-block;
  position: relative;
  width: 100%;
  padding-bottom: 1%;
  vertical-align: top;
  overflow: hidden;
}


#graphLayout {
  cursor: all-scroll;
}

#rectStream {
  cursor: pointer;
  stroke: lightgray;
  stroke-width: 2px;
}

.parcoordsSelection {
  stroke: black;
  stroke-width: 4px;
  stroke-dasharray: 20,5;
  /* transition: "visibility 0s, opacity 0.4s linear" */
}

.tagSelected {
  stroke: rgba(0, 255, 200, 0.4);
  stroke-width: 5px;

  fill: rgba(0, 255, 200, 0.4);
}

circle {
  cursor: pointer;
  stroke: lightgray;
  stroke-width: 2px;
}

text {
  font: 15px arial;
  pointer-events: none;
  opacity: 0.7;
}

text.shadow {
  stroke: #fff;
  stroke-width: 3px;
  stroke-opacity: 0.8;
}




.d3-context-menu {
  position: absolute;
  display: none;
  background-color: rgb(240, 240, 240);
  border-radius: 8px;
  box-shadow: rgb(73, 73, 73) 3px 3px 7px;
  font-family: Arial, sans-serif;
  font-size: 10px;
  min-width: 150px;
  border: 0px solid #d4d4d4;
  border: 0px solid #ffffff00;
  z-index: 1200;
  padding-top: 3px;
  padding-bottom: 3px;
}

.d3-context-menu ul {
  list-style-type: none;
  margin: 4px 0px;
  padding: 0px;
  cursor: pointer;
}

.d3-context-menu ul li {
  padding: 4px 16px;
}

.d3-context-menu ul li.stream:hover {
  background-color: hotpink;
  color: #fefefe;
  transition: 700ms;
}

.d3-context-menu ul li.client:hover {
  background-color: #0099ff;
  color: #fefefe;
  transition: 700ms;
}

.tooltip {
  position: absolute;
  text-align: center;
  width: 250px;
  height: 60px;
  padding: 2px;
  font: 12px sans-serif;
  background: #94e1ff;
  border: 2px;
  border-color: black;
  border-width: 2px;
  border-radius: 8px;
  pointer-events: none;
}

.tooltipOwner {
  position: absolute;
  text-align: center;
  width: 250px;
  height: 15px;
  padding: 2px;
  font: 12px sans-serif;
  background: #50ccfd;
  border: 0px;
  border-radius: 8px;
  pointer-events: none;
}

.tooltipDoc {
  position: absolute;
  text-align: center;
  width: 350px;
  height: 45px;
  padding: 2px;
  font: 12px sans-serif;
  background: hotpink;
  border: 0px;
  border-radius: 8px;
  pointer-events: none;
}

.hull {
  fill: steelblue;
  fill-opacity: 1;
  stroke: steelblue;
  stroke-width: 22px;
  stroke-opacity: 1;
  stroke-linejoin: round;
}

.subhullOwner {
  fill: rgb(126, 191, 243);
  stroke: rgb(126, 191, 243);
  stroke-width: 40;
  opacity: 0.5;
  stroke-linejoin: round;
}

.subhullDoc {
  fill: hotpink;
  stroke: hotpink;
  stroke-width: 40;
  opacity: 0.5;
  stroke-linejoin: round;
}

path.link {
  fill: none;
}

marker {
  stroke-width: 1000 !important;
}
.brush {
  stroke: #222;
  fill-opacity: 0.125;
  shape-rendering: crispEdges;
}
